  const [currentSection,changeCurrentSection] = useState(0);


  const test = (index) =>{changeCurrentSection(index);}

  return (

  	<div className= {"h-screen grid grid-cols-12 font-nunito"}>

  		<div className={"col-span-4"}>
  			<SideBar currentSection={currentSection} switcher={test} />
  		</div>

  		<div className={"col-span-8"}>

  			{currentSection == 0 ? 'Dashboard' : 'not immpl'}  

  		</div>

  	</div>

  );
}


export default App;
