import React from 'react';

const SideBarButton = (props) => {

				var className = props.selected ? 'flex gap-4 bg-white text-teal active:text-teal focus:text-teal px-8 py-5 rounded-lg fill-teal active:fill-teal focus:fill-teal'
										:

										'flex gap-4 hover:bg-bright-teal active:bg-white active:text-teal focus:text-teal px-8 py-5 rounded-lg fill-white active:fill-teal focus:fill-teal';


				return (
							<a className={className}>
								{props.icon}
								<span className={'grow font-bold'}>{props.name}</span>
							</a>
					);
}



export default SideBarButton;
