import React from 'react';

const SideBarButton = (props) => {

		var staticClassNames = 'flex flex-row gap-4 rounded-lg p-8';


		var dynamicClassNames = props.selected ? 'bg-white text-teal' : 'hover:bg-bright-teal active:bg-white active:text-teal focus:text-teal fill-white active:fill-teal focus:fill-teal';

		return (
				<a onClick={()=> { props.onClick(props.index); } } className={staticClassNames + ' ' + dynamicClassNames}>
					{props.icon}
					<span className={'grow font-bold'}>{props.name}</span>
				</a>
		);
}


export default SideBarButton;
