import React from 'react';

const SideBarButton = (props) => {

				return (
					props.selected ?
					<a className={'flex gap-4 hover:bg-bright-teal active:bg-white active:text-teal focus:text-teal px-8 py-5 rounded-lg fill-white active:fill-teal focus:fill-teal'}>
						{props.icon}
						<span className={'grow font-bold'}>{props.name}</span>
					</a>
					:
					<a className={'flex gap-4 hover:bg-bright-teal active:bg-white active:text-teal focus:text-teal px-8 py-5 rounded-lg fill-white active:fill-teal focus:fill-teal'}>
						{props.icon}
						<span className={'grow font-bold'}>{props.name}</span>
					</a>
				);

}



export default SideBarButton;
