import React from 'react';

const SideBarButton = (props) => {

		var staticClassNames = 'flex gap-4 rounded-lg px-8 py-5';


		var dynamicClassNames = props.selected ? 'bg-white text-teal fill-teal active:fill-teal focus:fill-teal' : ' hover:bg-bright-teal active:bg-white active:text-teal focus:text-teal fill-white active:fill-teal focus:fill-teal';

		return (
				<a className={staticClassNames + ' ' + dynamicClassNames}>
					{props.icon}
					<span className={'grow font-bold'}>{props.name}</span>
				</a>
		);
}


export default SideBarButton;
